import { useState } from "react";
import logo from "./logo.svg";

function App() {
  let [color, setColor] = useState("");

  return (
    <div className="App">
      <div>
        색상 선택
        <select
          onChange={(e) => {
            if (e.target.value != "") {
              setColor(e.target.value);
            } else {
              setColor();
            }
          }}
        >
          <option value="">선택</option>
          <option value="초록" style={{ background: "green" }}>
            초록
          </option>
          <option value="검정">검정</option>
        </select>
      </div>

      <div
        style={{ width: "100px", padding: "100px", backgroundColor: "{color}" }}
      >
        {color}
      </div>
    </div>
  );
}

export default App;
